package com.learn.simple;

public class L_686_Repeated_String_Match {
	public static void main(String[] arg0) {
		long a = System.currentTimeMillis();// 获取当前系统时间(毫秒)
		// String A="abcd";
		// String B ="cdabcdab";
		// String A= "abc";
		// String B = "cabcabca";
		String A = "a";
		String B = "";

		System.out.println(B.length());
		System.out.println(L_686_Repeated_String_Match.repeatedStringMatch(A, B));
		System.out.print("程序执行时间为:");
		System.out.println(System.currentTimeMillis() - a + "毫秒");
	}

	public static int repeatedStringMatch(String A, String B) {
		int i = 1;
		StringBuilder sb = new StringBuilder(A);
		while (sb.length() < B.length()) {
			sb.append(A);
			i++;
		}
		if (sb.toString().indexOf(B) != -1) {
			return i;
		}
		if (sb.append(A).toString().indexOf(B) != -1) {
			return ++i;
		}
		return -1;
	}
}
